import BodyWrapper from "./BodyWrapper";
import React from "react";
import { Button } from "reactstrap";
import { useDispatch, useSelector } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import { DELETE_ALL } from "../Todo/types";

const Body = (props) => {
  const dispatch = useDispatch();
  const data = useSelector((state) => state.tasks);
  const value = useSelector((state) => state.value);

  const add = () => {
    const action = { type: "ADD_TASK", payload: value };
    dispatch(action);
  };

  const deleteAll = (index) => {
    const action = { type: DELETE_ALL, payload: index };
    dispatch(action);
  };

  onkeydown = (event) => {
    if (event.keyCode == 13) add();
  };
  return (
    <BodyWrapper>

      <div className="plus">
        <h5>All {data.length}</h5>
        <Button className="plusButton" onClick={add}>
          <FontAwesomeIcon icon={faPlus} />
        </Button>
      </div>
      <Button className="fw-bold toy2" onClick={() => deleteAll()}>
        Clear All
      </Button>
    </BodyWrapper>
  );
};

export default Body;
