import BodyWrapper from "./BodyWrapper";
import React from "react";
import { Button } from "reactstrap";
import { useDispatch, useSelector } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";

const Body = (props) => {
  const dispatch = useDispatch();
  const value = useSelector((state) => state.value);

  const add = () => {
    const action = { type: "ADD_TASK", payload: value };
    dispatch(action);
  };

  const toggleCompleted = (index) => {
    console.log(index, "toggleCompleted");

    const action = { type: "TOGGLE_COMPLETED", payload: index };
    dispatch(action);
  };

  onkeydown = (event) => {
    if (event.keyCode == 13) add();
  };
  return (
    <BodyWrapper onDoubleClick={() => toggleCompleted(index)} key={index}>
      <div className="row justify-content-between">
        <div className="col-md-4 m-0">
          <h5>Bajarilganlar : {toggleCompleted.length}</h5>
        </div>
        <div className="col-md-4 p-0 m-0">
          <div className="plus">
            <Button className="plusButton" onClick={add}>
              <FontAwesomeIcon icon={faPlus} />
            </Button>
          </div>
        </div>
        <div className="col-md-4 p-0 m-0">
          <h5>Bajarilmaganlar : {add.length}</h5>
        </div>
      </div>
    </BodyWrapper>
  );
};

export default Body;
